mapper.select:
        select
id, name, type, value, min_amount, start_time, end_time, is_active, created_at, updated_at
        from discounts


                and id = #{id}


                and name = #{name}


                and type = #{type}


                and value = #{value}


                and min_amount = #{minAmount}


                and start_time = #{startTime}


                and end_time = #{endTime}


                and is_active = #{isActive}


                and created_at = #{createdAt}


                and updated_at = #{updatedAt}


        limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from discounts


        and id = #{id}


        and name = #{name}


        and type = #{type}


        and value = #{value}


        and min_amount = #{minAmount}


        and start_time = #{startTime}


        and end_time = #{endTime}


        and is_active = #{isActive}


        and created_at = #{createdAt}


        and updated_at = #{updatedAt}
