mapper.select:
        select
id, permission_name, description, created_at
        from permissions


                and id = #{id}


                and permission_name = #{permissionName}


                and description = #{description}


                and created_at = #{createdAt}


        limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from permissions


        and id = #{id}


        and permission_name = #{permissionName}


        and description = #{description}


        and created_at = #{createdAt}
