mapper.select:
        select
id, name, description, price, original_price, stock, category_id, image_url, is_active, created_at, updated_at
        from products


                and id = #{id}


                and name = #{name}


                and description = #{description}


                and price = #{price}


                and original_price = #{originalPrice}


                and stock = #{stock}


                and category_id = #{categoryId}


                and image_url = #{imageUrl}


                and is_active = #{isActive}


                and created_at = #{createdAt}


                and updated_at = #{updatedAt}


        limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from products


        and id = #{id}


        and name = #{name}


        and description = #{description}


        and price = #{price}


        and original_price = #{originalPrice}


        and stock = #{stock}


        and category_id = #{categoryId}


        and image_url = #{imageUrl}


        and is_active = #{isActive}


        and created_at = #{createdAt}


        and updated_at = #{updatedAt}
