mapper.select:
        select
id, username, password, real_name, phone, email, avatar_url, wechat_openid, role_id, is_active, created_at, updated_at
        from users


                and id = #{id}


                and username = #{username}


                and password = #{password}


                and real_name = #{realName}


                and phone = #{phone}


                and email = #{email}


                and avatar_url = #{avatarUrl}


                and wechat_openid = #{wechatOpenid}


                and role_id = #{roleId}


                and is_active = #{isActive}


                and created_at = #{createdAt}


                and updated_at = #{updatedAt}


        limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from users


        and id = #{id}


        and username = #{username}


        and password = #{password}


        and real_name = #{realName}


        and phone = #{phone}


        and email = #{email}


        and avatar_url = #{avatarUrl}


        and wechat_openid = #{wechatOpenid}


        and role_id = #{roleId}


        and is_active = #{isActive}


        and created_at = #{createdAt}


        and updated_at = #{updatedAt}
