mapper.select:
        select
id, order_no, user_id, total_amount, status, payment_method, payment_time, shipping_address_id, contact_phone, contact_name, remark, created_at, updated_at
        from orders


                and id = #{id}


                and order_no = #{orderNo}


                and user_id = #{userId}


                and total_amount = #{totalAmount}


                and status = #{status}


                and payment_method = #{paymentMethod}


                and payment_time = #{paymentTime}


                and shipping_address_id = #{shippingAddressId}


                and contact_phone = #{contactPhone}


                and contact_name = #{contactName}


                and remark = #{remark}


                and created_at = #{createdAt}


                and updated_at = #{updatedAt}


        limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from orders


        and id = #{id}


        and order_no = #{orderNo}


        and user_id = #{userId}


        and total_amount = #{totalAmount}


        and status = #{status}


        and payment_method = #{paymentMethod}


        and payment_time = #{paymentTime}


        and shipping_address_id = #{shippingAddressId}


        and contact_phone = #{contactPhone}


        and contact_name = #{contactName}


        and remark = #{remark}


        and created_at = #{createdAt}


        and updated_at = #{updatedAt}
