mapper.select:
        select
id, user_id, title, content, is_read, created_at
        from messages


                and id = #{id}


                and user_id = #{userId}


                and title = #{title}


                and content = #{content}


                and is_read = #{isRead}


                and created_at = #{createdAt}


        limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from messages


        and id = #{id}


        and user_id = #{userId}


        and title = #{title}


        and content = #{content}


        and is_read = #{isRead}


        and created_at = #{createdAt}
