mapper.select:
        select
id, name, contact_person, contact_phone, province, city, district, address_detail, zip_code, type, is_default, is_active, created_at, updated_at
        from product_addresses


                and id = #{id}


                and name = #{name}


                and contact_person = #{contactPerson}


                and contact_phone = #{contactPhone}


                and province = #{province}


                and city = #{city}


                and district = #{district}


                and address_detail = #{addressDetail}


                and zip_code = #{zipCode}


                and type = #{type}


                and is_default = #{isDefault}


                and is_active = #{isActive}


                and created_at = #{createdAt}


                and updated_at = #{updatedAt}


        limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from product_addresses


        and id = #{id}


        and name = #{name}


        and contact_person = #{contactPerson}


        and contact_phone = #{contactPhone}


        and province = #{province}


        and city = #{city}


        and district = #{district}


        and address_detail = #{addressDetail}


        and zip_code = #{zipCode}


        and type = #{type}


        and is_default = #{isDefault}


        and is_active = #{isActive}


        and created_at = #{createdAt}


        and updated_at = #{updatedAt}
