mapper.select:
        select
id, role_id, permission_id, created_at
        from role_permissions


                and id = #{id}


                and role_id = #{roleId}


                and permission_id = #{permissionId}


                and created_at = #{createdAt}


        limit #{pageable.offset}, #{pageable.pageSize}

        select
id, role_id, permission_id, created_at
        from role_permissions


                and id = #{id}


                and role_id = #{roleId}


                and permission_id = #{permissionId}


                and created_at = #{createdAt}

select count(1)
from role_permissions


        and id = #{id}


        and role_id = #{roleId}


        and permission_id = #{permissionId}


        and created_at = #{createdAt}
