mapper.select:
        select
id, user_id, recipient, phone, province, city, district, address_detail, is_default, created_at, updated_at
        from addresses


                and id = #{id}


                and user_id = #{userId}


                and recipient = #{recipient}


                and phone = #{phone}


                and province = #{province}


                and city = #{city}


                and district = #{district}


                and address_detail = #{addressDetail}


                and is_default = #{isDefault}


                and created_at = #{createdAt}


                and updated_at = #{updatedAt}


        limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from addresses


        and id = #{id}


        and user_id = #{userId}


        and recipient = #{recipient}


        and phone = #{phone}


        and province = #{province}


        and city = #{city}


        and district = #{district}


        and address_detail = #{addressDetail}


        and is_default = #{isDefault}


        and created_at = #{createdAt}


        and updated_at = #{updatedAt}
