mapper.select:
        select
id, user_id, product_id, quantity, selected, created_at, updated_at
        from shopping_cart


                and id = #{id}


                and user_id = #{userId}


                and product_id = #{productId}


                and quantity = #{quantity}


                and selected = #{selected}


                and created_at = #{createdAt}


                and updated_at = #{updatedAt}


        limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from shopping_cart


        and id = #{id}


        and user_id = #{userId}


        and product_id = #{productId}


        and quantity = #{quantity}


        and selected = #{selected}


        and created_at = #{createdAt}


        and updated_at = #{updatedAt}
